parameters.functionCode:
// Define your search parameters here
return {
  keywords: "software developer", // Job title or keywords
  location: "United States", // Location
  distance: "25", // Distance in miles
  timePosted: "24", // Time posted in hours (r24 = last 24 hours)
  maxResults: 10 // Maximum number of results to include in email
};

// Process the extracted HTML to get structured job data
const jobs = [];
const maxResults = $node["Set Job Search Parameters"].json.maxResults;
const extractedJobs = $input.all()[0].json.jobs;

for (let i = 0; i < Math.min(extractedJobs.length, maxResults); i++) {
  const jobHtml = extractedJobs[i];

  // Use regex to extract job information
  const titleMatch = jobHtml.match(/<h3 class=".*?">(.*?)<\/h3>/i);
  const companyMatch = jobHtml.match(/<a.*?class="job-search-card__subtitle-link.*?">(.*?)<\/a>/i);
  const locationMatch = jobHtml.match(/<span class="job-search-card__location">(.*?)<\/span>/i);
  const linkMatch = jobHtml.match(/<a class="job-search-card__link.*?href="(.*?)".*?>/i);
  const timeMatch = jobHtml.match(/<time.*?>(.*?)<\/time>/i);

  if (titleMatch && companyMatch) {
    jobs.push({
      title: titleMatch[1].trim(),
      company: companyMatch[1].trim(),
      location: locationMatch ? locationMatch[1].trim() : 'Not specified',
      link: linkMatch ? linkMatch[1].trim() : '',
      postedTime: timeMatch ? timeMatch[1].trim() : 'Recently',
    });
  }
}

return { jobs };

// Generate an HTML email with the job listings
const jobs = $input.all()[0].json.jobs;
const searchParams = $node["Set Job Search Parameters"].json;
const today = new Date().toLocaleDateString();

let emailHtml = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    h1 { color: #0077b5; }
    .job-item { border-bottom: 1px solid #eee; padding: 15px 0; }
    .job-title { color: #0077b5; font-weight: bold; margin-bottom: 5px; }
    .job-company { font-weight: bold; }
    .job-meta { color: #666; font-size: 0.9em; }
    .no-jobs { color: #666; font-style: italic; }
  </style>
</head>
<body>
  <div class="container">
    <h1>Your Daily LinkedIn Job Matches</h1>
    <p>Here are today's (${today}) job matches for: <strong>${searchParams.keywords}</strong> in <strong>${searchParams.location}</strong></p>
`;

if (jobs.length > 0) {
  jobs.forEach(job => {
    emailHtml += `
    <div class="job-item">
      <div class="job-title">${job.title}</div>
      <div class="job-company">${job.company}</div>
      <div class="job-meta">
        <span>${job.location}</span> â€¢ 
        <span>Posted: ${job.postedTime}</span>
      </div>
      <p><a href="${job.link || 'https://www.linkedin.com/jobs/'}" target="_blank">View Job on LinkedIn</a></p>
    </div>
    `;
  });
} else {
  emailHtml += `<p class="no-jobs">No jobs matching your criteria were found today. Try adjusting your search parameters.</p>`;
}

emailHtml += `
    <p>This email was sent automatically from your n8n LinkedIn Job Hunter.</p>
  </div>
</body>
</html>
`;

return {
  html: emailHtml,
  subject: `LinkedIn Job Matches: ${searchParams.keywords} (${today})`,
  jobs: jobs
};
